kp_vals = [0, .1, .2, .4, .8, 1, 1.3, 1.5 1.7, 2, 2.5];
[~,n] = size(kp_vals);
figure; hold on;
CM = lines(n);

% plot1 = [1]
plot1 = [2, 3, 4, 5];
[~,m] = size(plot1);
labels = cell(m,1);

for i=1:m
    idx = plot1(i);
    labels{i} = sprintf('kp = %.1f', kp_vals(idx));
    plot(data{idx},'color', CM(idx,:), 'linewidth', 1.5);
end

plot([170,170], [-10,70], 'k--') % when the robot starts to turn
plot([0,300], [0,0], 'k-')

title('Error value from P controller with varying K_p')
xlabel('Time')
ylabel('Error from color sensor')
legend(labels, 'Location', 'best')
saveas(figure(1), 'kp_lessthan1.jpg'); % Saves as a jpg
